package com.headstrong.fusion.commons;

/**
 * Represents the event generated by the components.
 */
public class ComponentEvent {
	private String componentId;
	private Event event;
	
	/**
	 * represents an event generated by a component.
	 * @param componentId
	 * @param event
	 */
	public ComponentEvent(String componentId, Event event) {
		this.componentId = componentId;
		this.event = event;
	}
	public static enum Event {
		NEW, //just when the process is registered and the routes are constructed 
		REGISTERED, //when producer and consumer instances are created
		INITIALIZED, //when producer and consumer are started and all initialization is done 
		STARTED, 
		ERROR
	}

	public String getComponentId() {
		return componentId;
	}

	public void setComponentId(String componentId) {
		this.componentId = componentId;
	}

	public Event getEvent() {
		return event;
	}

	public void setEvent(Event event) {
		this.event = event;
	}
}
